$(function () {
    $(".js-window-trigger").each(function () {
        $(this).addClass("is-active");
    }),
        $(".js-scroll-trigger").length &&
            ($(window).on("scroll", function () {
                $(".js-scroll-trigger").each(function () {
                    let t = $(this).offset().top,
                        o = $(window).scrollTop(),
                        s = $(window).height();
                    o > t - s + 80 && $(this).addClass("is-active");
                });
            }),
            $(window).trigger("scroll")),
        $(window).on("scroll", function t() {
            let o = $(window).scrollTop();
            o >= 50 ? $(".btn-top").addClass("active") : $(".btn-top").removeClass("active");
        }),
        $('a[href^="#"]').on("click", function (t) {
            t.preventDefault(), $("html, body").animate({ scrollTop: $($(this).attr("href")).offset().top }, 1e3);
        });
});
